INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('79', 'Moneymaker 2 Adaptation - Quantity +/- on any page with min/max ', 'Quantity +/- on any page with min/max control', 'sergey@oc-dev.ru, RGB', 'Pro v1.0', '', '<?xml version="1.0" encoding="UTF-8"?>\n<modification>\n<name>Moneymaker 2 Adaptation - Quantity +/- on any page with min/max control</name>\n<code>Quantity +/- on any page with min/max control</code>\n<version>Pro v1.0</version>\n<author>sergey@oc-dev.ru, RGB</author>\n\n<file path="catalog/controller/product/{category,search,special,manufacturer,latest,bestseller,mostviewed}*.php">\n\t<operation>\n\t<search><![CDATA[$result[\'product_id\'],]]></search>\n\t<add position="after" error="skip"><![CDATA[ \n\t\'quantity\' \t\t=> $result[\'quantity\'],\n\t\'min_quantity\'\t=> $result[\'minimum\'],\n\t\'price_value\'   => $result[\'price\'],\n    \'special_value\' => $result[\'special\'],\n\t]]></add>\n\t</operation>\n\t<operation>\n\t<search><![CDATA[$product_info[\'product_id\'],]]></search>\n\t<add position="after" error="skip"><![CDATA[ \n\t\'quantity\' \t\t=> $product_info[\'quantity\'],\n\t\'min_quantity\'\t=> $product_info[\'minimum\'],\n\t\'price_value\'   => $product_info[\'price\'],\n    \'special_value\' => $product_info[\'special\'],\n\t]]></add>\n\t</operation>\n</file>\n\n\n<file path="catalog/controller/common/footer.php">\n\t<operation>\n\t<search><![CDATA[ $this->load->model(\'catalog/information\'); ]]></search>\n\t<add position="before" error="skip">\n    <![CDATA[\n\t$this->document->addStyle(\'catalog/view/javascript/quantity_control/quantity_control.css?v=1.0\');\n\t$this->document->addScript(\'catalog/view/javascript/quantity_control/quantity_control.js?v=1.0\');\n    $var_currency = array();\n    $var_currency[\'value\'] = $this->currency->getValue($this->session->data[\'currency\']);\n    $var_currency[\'symbol_left\'] = $this->currency->getSymbolLeft($this->session->data[\'currency\']);\n    $var_currency[\'symbol_right\'] = $this->currency->getSymbolRight($this->session->data[\'currency\']);\n    $var_currency[\'decimals\'] = $this->currency->getDecimalPlace($this->session->data[\'currency\']);\n    $var_currency[\'decimal_point\'] = $this->language->get(\'decimal_point\');\n    $var_currency[\'thousand_point\'] = $this->language->get(\'thousand_point\');\n    $data[\'currency\'] = $var_currency;\n\t$data[\'config_stock\'] = $this->config->get(\'config_stock_checkout\');\n    ]]>\n    </add>\n\t</operation>\n</file>\n\t\n<file path="catalog/view/theme/moneymaker2/template/product/{category,search,special,manufacturer}*.tpl">\n\t<operation>\n\t<search><![CDATA[<div class="additional"><?php echo $product[\'stock\']; ?>]]></search>\n\t<add position="before" error="skip"><![CDATA[\n\t<div class="btn-group btn-group-sm quantity_div"><button type="button" class="btn btn-link" onclick="quantity_control(\'<?php echo $product[\'product_id\']; ?>\', \'<?php echo $product[\'min_quantity\']; ?>\', \'<?php echo $product[\'quantity\']; ?>\', \'-\')"><i class="fa fa-fw">&ndash;</i></button><input type="text" data-maximum="<?php echo $product[\'quantity\']; ?>" name="quantity" value="<?php echo $product[\'min_quantity\']; ?>" data-id="<?php echo $product[\'product_id\']; ?>" size="2" class="form-control quantity_input p_q_<?php echo $product[\'product_id\']; ?>" oninput="all_inputs();"><button type="button" class="btn btn-link" onclick="quantity_control(\'<?php echo $product[\'product_id\']; ?>\', \'<?php echo $product[\'min_quantity\']; ?>\', \'<?php echo $product[\'quantity\']; ?>\', \'+\')"><i class="fa fa-fw">+</i></button><div class="stock_warning s_w_<?php echo $product[\'product_id\']; ?>"></div></div>\n\t]]></add>\n\t</operation>\n\t<operation>\n\t<search><![CDATA[cart.add(\'<?php echo $product[\'product_id\']; ?>\', \'<?php echo $product[\'minimum\']; ?>\')]]></search>\n\t<add position="replace" error="skip"><![CDATA[cart.add(\'<?php echo $product[\'product_id\']; ?>\', $(this).parent().parent().parent().find(\'.quantity_input\').val())]]></add>\n\t</operation>\n\n\t<operation>\n    <search><![CDATA["><?php if (!$product[\'special\']) { ?><?php echo $product[\'price\']; ?>]]></search>\n    <add position="replace" error="skip"><![CDATA["><?php if (!$product[\'special\']) { ?><span class="price_<?php echo $product[\'product_id\']; ?>" data-price="<?php echo $product[\'price_value\']; ?>"><?php echo $product[\'price\']; /**/ ?></span>]]></add>\n    </operation>\n\n\t<operation>\n    <search><![CDATA[<span class="price-old"><?php echo $product[\'price\']; ?>]]></search>\n    <add position="replace" error="skip"><![CDATA[<span class="price-old"><span class="price_<?php echo $product[\'product_id\']; ?>" data-price="<?php echo $product[\'price_value\']; ?>"><?php echo $product[\'price\']; /**/ ?></span>]]></add>\n    </operation>\n\n\n    <operation>\n    <search><![CDATA[<span class="price-new"><?php echo $product[\'special\']; ?>]]></search>\n    <add position="replace" error="skip"><![CDATA[<span class="price-new"><span class="special_<?php echo $product[\'product_id\']; ?>" data-price="<?php echo $product[\'special_value\']; ?>"><?php echo $product[\'special\']; /**/ ?></span>]]></add>\n    </operation>\n    <operation>\n    <search><![CDATA[<span class="price-new"><b><?php echo $product[\'special\']; ?>]]></search>\n    <add position="replace" error="skip"><![CDATA[<span class="price-new"><b><span class="special_<?php echo $product[\'product_id\']; ?>" data-price="<?php echo $product[\'special_value\']; ?>"><?php echo $product[\'special\']; /**/ ?></span>]]></add>\n    </operation>\n</file>\n\n<file path="catalog/view/theme/moneymaker2/template/common/footer.tpl">\n\t<operation>\n\t<search><![CDATA[</body>]]></search>\n\t<add position="before" error="skip"><![CDATA[\n\t<?php if($config_stock) { ?>\n\t\t<input type="hidden" name="qcs" value="1" />\n\t<?php } ?>\n\t<script>\t\n\tfunction all_inputs() {\n\t\t$(\'input[name^=quantity]\').each(function() {\n\t\t\tvar product_id = $(this).attr(\'data-id\');\n\t\t\tvar minimum = Number($(this).val());\n<?php if($config_stock) { ?>\n\t\t\tvar maximum = 9999;\n<?php } else { ?>\n\t\t\tvar maximum = Number($(this).attr(\'data-maximum\'));\n<?php } ?>\n\t\t\tquantity_control(product_id ,minimum, maximum);\n\n\t\t\tif (minimum > 0) {\n\t\t\t\tcalculate_price(product_id, minimum);\n\t\t\t}\n\t\t\t\n\t\t\t$(this).bind(\'input\', function() {\n\t\t\t\tvar value2 = $(this).val();\n\t\t\t\tif (~~value2 > maximum) {\n\t\t\t\t\t$(this).val(maximum);\n\t\t\t\t\tquantity_alert(product_id, maximum);\n\t\t\t\t}\n\t\t\t\tcalculate_price(product_id, $(this).val())\n\t\t\t});\n\t\t\t\n\t\t\tif(maximum <= 0) {\n\t\t\t\t$(\'.p_q_\'+ product_id).val(\'0\');\n\t\t\t\t$(\'.p_q_\'+ product_id).parent().parent().find(\'.button\').attr(\'disabled\', \'disabled\');\n\t\t\t}\n\t\t});\n\t}\n\t\n\tfunction price_format(n){ \t\n\t\tc = <?php echo (empty($currency[\'decimals\']) ? "0" : $currency[\'decimals\'] ); ?>;\n\t\td = \'<?php echo $currency[\'decimal_point\']; ?>\';\n\t\tt = \'<?php echo $currency[\'thousand_point\']; ?>\';\n\t\ts_left = \'<?php echo $currency[\'symbol_left\']; ?>\';\n\t\ts_right = \'<?php echo $currency[\'symbol_right\']; ?>\';\n\t\tn = n * <?php echo $currency[\'value\']; ?>;\n\t\ti = parseInt(n = Math.abs(n).toFixed(c)) + \'\'; \n\t\tj = ((j = i.length) > 3) ? j % 3 : 0; \n\t\treturn s_left + (j ? i.substr(0, j) + t : \'\') + i.substr(j).replace(/(\\d{3})(?=\\d)/g, "$1" + t) + (c ? d + Math.abs(n - i).toFixed(c).slice(2) : \'\') + s_right; \n\t}\n\t</script>\n\t]]></add>\n\t</operation>\n</file>\n\n<file path="catalog/view/theme/moneymaker2/template/common/footer.tpl">\n\t<operation>\n\t<search><![CDATA[var price = target.data(\'order-price\') && target.data(\'order-price\').toString().replace(":", " ");]]></search>\n\t<add position="replace"><![CDATA[var price = target.parent().parent().find(\'span.price\').html(); if (typeof price === \'undefined\') { var price = target.data(\'order-price\') && target.data(\'order-price\').toString().replace(":", " ");}]]></add>\n\t</operation>\n\t<operation>\n\t<search><![CDATA[if (mode=="product") {]]></search>\n\t<add position="before"><![CDATA[\n\t  if (mode=="catalog") {\n\t\tvar quantity_control_value = target.parent().parent().find(\'input[name=\\\'quantity\\\']\').val();\n\t\tif (quantity_control_value!==0&&typeof quantity_control_value!==\'undefined\') {\n\t\t  modal.find(\'.modal-body input[type=\\\'hidden\\\']\').val(quantity_control_value);\n\t\t} else {\n\t\t  modal.find(\'.modal-body input[type=\\\'hidden\\\']\').val(1);\n\t\t}\n\t  }\n]]></add>\n\t</operation>\n</file>\n\n</modification>', '0', '2019-07-23 23:41:59');
