INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('43', '2', 'MacBook', '&lt;div&gt;\t&lt;p&gt;\t\t&lt;b&gt;Intel Core 2 Duo processor&lt;/b&gt;&lt;/p&gt;\t&lt;p&gt;\t\tPowered by an Intel Core 2 Duo processor at speeds up to 2.16GHz, the new MacBook is the fastest ever.&lt;/p&gt;\t&lt;p&gt;\t\t&lt;b&gt;1GB memory, larger hard drives&lt;/b&gt;&lt;/p&gt;\t&lt;p&gt;\t\tThe new MacBook now comes with 1GB of memory standard and larger hard drives for the entire line perfect for running more of your favorite applications and storing growing media collections.&lt;/p&gt;\t&lt;p&gt;\t\t&lt;b&gt;Sleek, 1.08-inch-thin design&lt;/b&gt;&lt;/p&gt;\t&lt;p&gt;\t\tMacBook makes it easy to hit the road thanks to its tough polycarbonate case, built-in wireless technologies, and innovative MagSafe Power Adapter that releases automatically if someone accidentally trips on the cord.&lt;/p&gt;\t&lt;p&gt;\t\t&lt;b&gt;Built-in iSight camera&lt;/b&gt;&lt;/p&gt;\t&lt;p&gt;\t\tRight out of the box, you can have a video chat with friends or family,2 record a video at your desk, or take fun pictures with Photo Booth&lt;/p&gt;&lt;/div&gt;', '', 'MacBook', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('44', '1', 'MacBook Air', '&lt;div&gt;\tMacBook Air is ultrathin, ultraportable, and ultra unlike anything else. But you don&amp;rsquo;t lose inches and pounds overnight. It&amp;rsquo;s the result of rethinking conventions. Of multiple wireless innovations. And of breakthrough design. With MacBook Air, mobile computing suddenly has a new standard.&lt;/div&gt;', '', 'MacBook Air', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('45', '2', 'MacBook Pro', '&lt;div class=&quot;cpt_product_description &quot;&gt;\t&lt;div&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Latest Intel mobile architecture&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tPowered by the most advanced mobile processors from Intel, the new Core 2 Duo MacBook Pro is over 50% faster than the original Core Duo MacBook Pro and now supports up to 4GB of RAM.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Leading-edge graphics&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tThe NVIDIA GeForce 8600M GT delivers exceptional graphics processing power. For the ultimate creative canvas, you can even configure the 17-inch model with a 1920-by-1200 resolution display.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Designed for life on the road&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tInnovations such as a magnetic power connection and an illuminated keyboard with ambient light sensor put the MacBook Pro in a class by itself.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Connect. Create. Communicate.&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tQuickly set up a video conference with the built-in iSight camera. Control presentations and media from up to 30 feet away with the included Apple Remote. Connect to high-bandwidth peripherals with FireWire 800 and DVI.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Next-generation wireless&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tFeaturing 802.11n wireless technology, the MacBook Pro delivers up to five times the performance and up to twice the range of previous-generation technologies.&lt;/p&gt;\t&lt;/div&gt;&lt;/div&gt;&lt;!-- cpt_container_end --&gt;', '', 'MacBook Pro', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('45', '1', 'MacBook Pro', '&lt;div class=&quot;cpt_product_description &quot;&gt;\t&lt;div&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Latest Intel mobile architecture&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tPowered by the most advanced mobile processors from Intel, the new Core 2 Duo MacBook Pro is over 50% faster than the original Core Duo MacBook Pro and now supports up to 4GB of RAM.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Leading-edge graphics&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tThe NVIDIA GeForce 8600M GT delivers exceptional graphics processing power. For the ultimate creative canvas, you can even configure the 17-inch model with a 1920-by-1200 resolution display.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Designed for life on the road&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tInnovations such as a magnetic power connection and an illuminated keyboard with ambient light sensor put the MacBook Pro in a class by itself.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Connect. Create. Communicate.&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tQuickly set up a video conference with the built-in iSight camera. Control presentations and media from up to 30 feet away with the included Apple Remote. Connect to high-bandwidth peripherals with FireWire 800 and DVI.&lt;/p&gt;\t\t&lt;p&gt;\t\t\t&lt;b&gt;Next-generation wireless&lt;/b&gt;&lt;/p&gt;\t\t&lt;p&gt;\t\t\tFeaturing 802.11n wireless technology, the MacBook Pro delivers up to five times the performance and up to twice the range of previous-generation technologies.&lt;/p&gt;\t&lt;/div&gt;&lt;/div&gt;&lt;!-- cpt_container_end --&gt;', '', 'MacBook Pro', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('31', '2', 'Nikon D300', '&lt;div class=&quot;cpt_product_description &quot;&gt;\t&lt;div&gt;\t\tEngineered with pro-level features and performance, the 12.3-effective-megapixel D300 combines brand new technologies with advanced features inherited from Nikon\'s newly announced D3 professional digital SLR camera to offer serious photographers remarkable performance combined with agility.&lt;br&gt;\t\t&lt;br&gt;\t\tSimilar to the D3, the D300 features Nikon\'s exclusive EXPEED Image Processing System that is central to driving the speed and processing power needed for many of the camera\'s new features. The D300 features a new 51-point autofocus system with Nikon\'s 3D Focus Tracking feature and two new LiveView shooting modes that allow users to frame a photograph using the camera\'s high-resolution LCD monitor. The D300 shares a similar Scene Recognition System as is found in the D3; it promises to greatly enhance the accuracy of autofocus, autoexposure, and auto white balance by recognizing the subject or scene being photographed and applying this information to the calculations for the three functions.&lt;br&gt;\t\t&lt;br&gt;\t\tThe D300 reacts with lightning speed, powering up in a mere 0.13 seconds and shooting with an imperceptible 45-millisecond shutter release lag time. The D300 is capable of shooting at a rapid six frames per second and can go as fast as eight frames per second when using the optional MB-D10 multi-power battery pack. In continuous bursts, the D300 can shoot up to 100 shots at full 12.3-megapixel resolution. (NORMAL-LARGE image setting, using a SanDisk Extreme IV 1GB CompactFlash card.)&lt;br&gt;\t\t&lt;br&gt;\t\tThe D300 incorporates a range of innovative technologies and features that will significantly improve the accuracy, control, and performance photographers can get from their equipment. Its new Scene Recognition System advances the use of Nikon\'s acclaimed 1,005-segment sensor to recognize colors and light patterns that help the camera determine the subject and the type of scene being photographed before a picture is taken. This information is used to improve the accuracy of autofocus, autoexposure, and auto white balance functions in the D300. For example, the camera can track moving subjects better and by identifying them, it can also automatically select focus points faster and with greater accuracy. It can also analyze highlights and more accurately determine exposure, as well as infer light sources to deliver more accurate white balance detection.&lt;/div&gt;&lt;/div&gt;&lt;!-- cpt_container_end --&gt;', '', 'Nikon D300', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('29', '2', 'Palm Treo Pro', '&lt;p&gt;\tRedefine your workday with the Palm Treo Pro smartphone. Perfectly balanced, you can respond to business and personal email, stay on top of appointments and contacts, and use Wi-Fi or GPS when you’re out and about. Then watch a video on YouTube, catch up with news and sports on the web, or listen to a few songs. Balance your work and play the way you like it, with the Palm Treo Pro.&lt;/p&gt;&lt;p&gt;\t&lt;strong&gt;Features&lt;/strong&gt;&lt;/p&gt;&lt;ul&gt;\t&lt;li&gt;\t\tWindows Mobile® 6.1 Professional Edition&lt;/li&gt;\t&lt;li&gt;\t\tQualcomm® MSM7201 400MHz Processor&lt;/li&gt;\t&lt;li&gt;\t\t320x320 transflective colour TFT touchscreen&lt;/li&gt;\t&lt;li&gt;\t\tHSDPA/UMTS/EDGE/GPRS/GSM radio&lt;/li&gt;\t&lt;li&gt;\t\tTri-band UMTS — 850MHz, 1900MHz, 2100MHz&lt;/li&gt;\t&lt;li&gt;\t\tQuad-band GSM — 850/900/1800/1900&lt;/li&gt;\t&lt;li&gt;\t\t802.11b/g with WPA, WPA2, and 801.1x authentication&lt;/li&gt;\t&lt;li&gt;\t\tBuilt-in GPS&lt;/li&gt;\t&lt;li&gt;\t\tBluetooth Version: 2.0 + Enhanced Data Rate&lt;/li&gt;\t&lt;li&gt;\t\t256MB storage (100MB user available), 128MB RAM&lt;/li&gt;\t&lt;li&gt;\t\t2.0 megapixel camera, up to 8x digital zoom and video capture&lt;/li&gt;\t&lt;li&gt;\t\tRemovable, rechargeable 1500mAh lithium-ion battery&lt;/li&gt;\t&lt;li&gt;\t\tUp to 5.0 hours talk time and up to 250 hours standby&lt;/li&gt;\t&lt;li&gt;\t\tMicroSDHC card expansion (up to 32GB supported)&lt;/li&gt;\t&lt;li&gt;\t\tMicroUSB 2.0 for synchronization and charging&lt;/li&gt;\t&lt;li&gt;\t\t3.5mm stereo headset jack&lt;/li&gt;\t&lt;li&gt;\t\t60mm (W) x 114mm (L) x 13.5mm (D) / 133g&lt;/li&gt;&lt;/ul&gt;', '', 'Palm Treo Pro', '', '');
