INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES ('103', '1', 'JBL EON 612', '&lt;p&gt;More than just another great JBL sound system, the new EON600 is a true step forward in technology developed specifically to deliver &lt;strong&gt;the best sound possible&lt;/strong&gt; regardless of its application.&lt;/p&gt;&lt;p&gt;Rethinking how truly good an affordable self-contained, portable PA system can be, &lt;a href=&quot;jbl&quot;&gt;JBL engineers&lt;/a&gt; designed and built the EON600 from the ground up featuring &lt;strong&gt;waveguide technology&lt;/strong&gt;, JBL designed and manufactured transducers, and wireless remote control of its onboard DSP EQ parameters via Bluetooth.&lt;/p&gt;&lt;ul class=&quot;circle&quot;&gt;&lt;li&gt;Purposeful Product Design&lt;/li&gt;&lt;li&gt;Advanced Waveguide Technology&lt;/li&gt;&lt;li&gt;Bluetooth Integration&lt;/li&gt;&lt;li&gt;JBL designed and manufactured transducers&lt;/li&gt;&lt;li&gt;Remote control of onboard DSP EQ&lt;/li&gt;&lt;/ul&gt;&lt;h3 class=&quot;text-center&quot;&gt;12&quot; Two-Way Multipurpose Self-Powered&lt;/h3&gt;&lt;p&gt;This total redesign of the &lt;a href=&quot;index.php?route=product/category&amp;amp;path=103_134&quot;&gt;JBL EON platform&lt;/a&gt; leverages the latest technologies in the following things:&lt;/p&gt;&lt;ol&gt;&lt;li&gt;Cabinet materials&lt;/li&gt;&lt;li&gt;Acoustic science, transducer design &lt;/li&gt;&lt;li&gt;User friendliness and expirience&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;It delivers the extraordinary quality of a &lt;strong&gt;high-end studio monitor&lt;/strong&gt; in a fully professional, highly flexible, easy to use, portable system for today’s working musicians and sound providers.&lt;/p&gt;&lt;h3 class=&quot;text-center&quot;&gt;Lift-assist Aluminum Tripod Speaker Stand&lt;/h3&gt;&lt;p&gt;The JBLTRIPOD-GA is an incredibly easy speaker stand to use thanks to the internal pneumatic shock special system that literally raises the speaker for you. The stand’s internal shock easily raises speakers weighing 35 lbs and less with absolutely no effort from the user.&lt;/p&gt;&lt;p class=&quot;text-center&quot;&gt;&lt;img alt=&quot;eon500_applications&quot; src=&quot;image/catalog/sound/eon500_applications.jpg&quot;&gt;&lt;/p&gt;', '', 'JBL EON 612', '', '');
